class ImageClassifDataModule(BaseDataModule):
    def __init__(
        self,
        datafolder: Path,
        data_csv: Path,
        classes: Sequence[str],
        label_func: Callable[[Path], str],
        sample_mode: int = 0,
        **kwargs
    ):
        super().__init__(datafolder, data_csv, **kwargs)
        self.classes = classes
        self.n_classes = len(classes)
        self.sample_mode = sample_mode
        self.label_func = label_func

    def setup(self, stage: Optional[str] = None):
        self.filt = self.get_filt()
        data = ImageClassifDataset.from_folder(
            self.datafolder,
            self.label_func,
            classes=self.classes,
            extensions=[".png"],
            include=self.classes,
            filterfunc=self.filt,
            train_percent=self.train_percent,
        ).split_by_csv(self.data_csv, get_id=self.get_id)
        n = len(data.train)
        self.weights = [
            np.float32(n / (data.train.labels == cl).sum()) for cl in self.classes
        ]
        self.data = data.to_tensor(tfms=self.tfms, tfm_y=False)

    def train_dataloader(self) -> DataLoader:
        sm = self.sample_mode
        w = self.weights
        labels = self.data.train.labels
        if sm > 0:
            weights = np.ones_like(labels, dtype=np.float32)
            for k, cl in enumerate(self.classes):
                weights[labels == cl] = w[k]
            if sm == 1:
                n = max([(labels == cl).sum() for cm in self.classes])
                sampler = WeightedRandomSampler(weights, int(self.n_classes * n))
            else:
                n = min([(labels == cl).sum() for cm in self.classes])
                sampler = WeightedRandomSampler(
                    weights, int(self.n_classes * n), replacement=False
                )
        else:
            sampler = RandomSampler(self.data.train)
        return DataLoader(
            self.data.train,
            batch_size=self.batch_size,
            sampler=sampler,
            drop_last=True,
            num_workers=4,
            pin_memory=True,
        )

