class ClassifModel(BaseModule):
    """"""

    def __init__(
        self, n_classes: int = 2, classes: Optional[Sequence[str]] = None, **kwargs
    ):
        super().__init__(**kwargs)
        classes = ifnone(classes, list(range(n_classes)))
        n_classes = len(classes)  # if classes is specified it superseeds n_classes
        self.save_hyperparameters()
        hparams = self.hparams
        self.loss = _get_loss(
            hparams.loss, 1.0, hparams.reduction, device=self.main_device, nc=n_classes
        )
        self.base_model = get_base_model(
            hparams.model, pretrained=not hparams.rand_weights
        )
        nf = get_num_features(self.base_model)
        self.head = get_head(nf, n_classes, dropout=hparams.dropout)
        self.post_init()
        self._create_normalizer()

    def validation_step(
        self, batch: Tuple[torch.Tensor, torch.Tensor], batch_nb: int
    ) -> Dict[str, torch.Tensor]:
        # OPTIONAL
        x, y = batch
        y_hat = self(x).detach()
        loss = self.loss(y_hat, y).detach()
        ret = {"loss": loss}
        n = y.shape[0]
        y_hat = torch.softmax(y_hat, dim=1)
        y_hat = y_hat.argmax(dim=-1).view(n, -1)
        y = y.view(n, -1)
        for k, cl in enumerate(self.hparams.classes):
            ret[f"t_{cl}"] = ((y_hat == k) & (y == k)).float().sum()
            ret[f"f_{cl}"] = ((y_hat == k) & (y != k)).float().sum()
        return ret

    def validation_epoch_end(self, outputs: List[Dict[str, torch.Tensor]]):
        # OPTIONAL
        log = {}
        t_keys = [f"t_{cl}" for cl in self.hparams.classes]
        f_keys = [f"f_{cl}" for cl in self.hparams.classes]
        t = _get_keys_in_list_and_apply(outputs, *t_keys, apply_func=sum)
        f = _get_keys_in_list_and_apply(outputs, *f_keys, apply_func=sum)
        t = torch.stack(t)
        f = torch.stack(f)
        for metric in self.metrics:
            try:
                name = metric.__name__
            except AttributeError:
                name = metric.func.__name__
                kws = metric.keywords
                for k in kws:
                    name += f"_{k}_{kws[k]}"
            for k, cl in enumerate(self.hparams.classes):
                c_name = name + f"_{cl}"
                log[c_name] = metric(
                    t[k],
                    f[k],
                    t[:k].sum() + t[k + 1 :].sum(),
                    f[:k].sum() + f[k + 1 :].sum(),
                )
        log["val_loss"] = torch.mean(
            torch.stack([output["loss"] for output in outputs])
        )
        self.log_dict(log)

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        x = _open_functions[self.hparams.open_mode](x).detach()
        if hasattr(self, "norm"):
            x = self.norm(x)
        x = self.base_model(x)
        x = self.head(x)
        return x

    def predict(self, x: torch.Tensor) -> torch.Tensor:
        pred = super().predict(x)
        pred = torch.softmax(pred, dim=1)
        return pred

    def freeze_base(self):
        """
        Freeze the base model.
        """
        for m in self.leaf_modules:
            if "base_model" in m.name and not isinstance(m, nn.BatchNorm2d):
                for param in m.parameters():
                    param.requires_grad = False

