class RNNAttention(BaseModule):
    """"""

    def __init__(
        self, n_glimpses: int = 3, glimpse_size: int = 256, gamma: float = 1, **kwargs
    ):
        super().__init__(**kwargs)
        hparams = self.hparams
        self.loss = _get_loss(
            hparams.loss, 1.0, hparams.reduction, device=self.main_device
        )
        nc = 2 if hparams.loss == "cross-entropy" else 1
        self.t_x = nn.Sequential(*list(CBR(3, 64, 2).children())[:-1])
        nf = get_num_features(self.t_x)
        self.fc = nn.Linear(nx, nc)
        self.t_l = nn.Sequential(
            nn.Linear(6, nf),
            nn.ReLU(),
            nn.Linear(nf, 2 * nf),
            nn.ReLU(),
            nn.Linear(2 * nf, nf),
        )
        self.t_a = nn.Sequential(
            nn.Linear(nf, 2 * nf),
            nn.ReLU(),
            nn.Linear(2 * nf, nf),
            nn.ReLU(),
            nn.Linear(nf, 6),
        )
        self.final_head = nn.Sequential(
            nn.Linear(nx * n_glimpses, nx), nn.ReLU(), nn.Linear(nf, nc)
        )
        self.save_hyperparameters()

    def forward(
        self, X: torch.Tensor, l: torch.Tensor
    ) -> Tuple[torch.Tensor, torch.Tensor, torch.Tensor]:
        X = _open_functions[self.hparams.open_mode](X).detach()
        Ai = gaussian_mask(*l[:3], self.hparams.glimpse_size, self.hparams.size)
        Aj = gaussian_mask(*l[3:], self.hparams.glimpse_size, self.hparams.size)
        x = Ai[None, None] @ X @ Aj.T[None, None]
        if hasattr(self, "norm"):
            x = self.norm(x)
        fx = self.t_x(x)
        y = self.fc(fx)
        fl = self.t_l(l)
        l = torch.sigmoid(fx * fl)
        l = self.t_a(l)
        return fx, y, l

    def compute_loss(
        self, X: torch.Tensor, Y: torch.Tensor
    ) -> Tuple[torch.Tensor, torch.Tensor]:
        """
        Compute the loss defined in the paper. Return the final slide-level
        prediction as well.
        """
        l0 = torch.tensor(
            [0, 0.5, self.hparams.size // self.hparams.glimpse_size] * 2,
            device=self.main_device,
        )
        loss = 0
        loss_prev = 0
        preds = []
        for t in range(self.hparams.n_glimpses):
            fx, y_hat, l = self(X, l0)
            fts.append(fx)
            loss += self.loss(y_hat, Y)
            loss_a = (y_hat ** 2).sum()
            if t > 0:
                loss -= loss_a - loss_prev / t
            loss_prev += loss_a
            loss += self.hparams.gamma * torch.exp(-torch.abs(l - l0))
            l0 = l.detach()
        # n_glimpses x bs x C
        fts = torch.cat(fts)
        fts = fts.view(fts.shape[1], -1)
        Y_hat = self.final_head(fts)
        loss += self.loss(Y_hat, Y)
        return loss, Y_hat

    def training_step(
        self, batch: Tuple[torch.Tensor, torch.Tensor], batch_nb: int
    ) -> Dict[str, torch.Tensor]:
        # REQUIRED
        X, Y = batch
        loss, _ = self.compute_loss(X, Y)
        lr = self.sched.optimizer.param_groups[-1]["lr"]
        log = {"train_loss": loss, "learning_rate": lr}
        self.log_dict(log)
        return {"loss": loss}

    def validation_step(
        self, batch: Tuple[torch.Tensor, torch.Tensor], batch_nb: int
    ) -> Dict[str, torch.Tensor]:
        # OPTIONAL
        x, y = batch
        y_hat = self(x)
        loss = self.loss(y_hat, y)
        ret = {"loss": loss}
        n = y.shape[0]
        if self.hparams.loss == "cross-entropy":
            y_hat = torch.softmax(y_hat, dim=1)
            y_hat = y_hat.argmax(dim=-1).view(n, -1)
        else:
            y_hat = torch.sigmoid(y_hat)
            y_hat = (y_hat > 0.5).view(n, -1)
        y = y.view(n, -1)
        ret["tp"] = ((y_hat) & (y == 1)).float().sum()
        ret["tn"] = ((~y_hat) & (y == 0)).float().sum()
        ret["fp"] = ((y_hat) & (y == 0)).float().sum()
        ret["fn"] = ((~y_hat) & (y == 1)).float().sum()
        return ret

    def validation_epoch_end(self, outputs: List[Dict[str, torch.Tensor]]):
        # OPTIONAL
        tp, fp, tn, fn = _get_keys_in_list_and_apply(
            outputs, "tp", "fp", "tn", "fn", apply_func=torch.sum
        )
        log = {}
        for metric in self.metrics:
            try:
                name = metric.__name__
            except AttributeError:
                name = metric.func.__name__
                kws = metric.keywords
                for k in kws:
                    name += f"_{k}_{kws[k]}"
            log[name] = metric(tp, fp, tn, fn)
        log["val_loss"] = torch.mean([output["loss"] for output in outputs])
        self.log_dict(log)

# Cell
class MILDataModule(BaseDataModule):
    def __init__(
        self,
        datafolder: Union[str, Path],
        data_csv: Union[str, Path],
        coord_csv: Union[str, Path],
        classes: Sequence[str],
        label_func: Optional[Callable[[Path], str]] = None,
        extensions: Optional[Sequence[str]] = None,
        level: int = 1,
        sample_mode: int = 0,
        **kwargs
    ):
        super().__init__(datafolder, data_csv, **kwargs)
        assert len(classes) == 2, "MIL only works with 2 classes."
        self.sample_mode = sample_mode
        self.coord_csv = coord_csv
        self.classes = classes
        self.label_func = ifnone(label_func, lambda x: x.parent.name)
        self.extensions = ifnone(extensions, ['.mrxs'])
        self.level = level

    def setup(self, stage: Optional[str] = None):
        self.data = (
            MILDataset.from_folder(
                self.datafolder,
                self.label_func,
                self.coord_csv,
                classes=self.classes,
                extensions=self.extensions,
                include=self.classes,
                train_percent=self.train_percent,
                size=self.size,
                level=self.level
            )
            .split_by_csv(self.data_csv)
            .to_tensor(tfms=self.tfms, tfm_y=False)
        )
        weights = np.float32(
            (self.data.train.labels == self.classes[0]).sum()
            / (self.data.train.labels == self.classes[1]).sum()
        )
        min_class = 1
        if weights < 1:
            weights = 1 / weights
            min_class = 0
        self.min_class = min_class
        self.weights = weights

    def train_dataloader(self) -> DataLoader:
        sm = self.sample_mode
        w = self.weights
        if sm > 0:
            labels = self.data.train.labels == self.classes[self.min_class]
            weights = np.where(labels, w, 1.0)
            if sm == 1:
                sampler = WeightedRandomSampler(weights, 2 * len(np.argwhere(~labels)))
            else:
                sampler = WeightedRandomSampler(weights,
                                                2*len(np.argwhere(labels)),
                                                replacement=False)
        else:
            sampler = RandomSampler(self.data.train)
        return DataLoader(
            self.data.train,
            batch_size=self.batch_size,
            sampler=sampler,
            drop_last=True,
            pin_memory=True,
        )

    def val_dataloader(self) -> DataLoader:
        # OPTIONAL
        # can also return a list of val dataloaders
        return DataLoader(
            self.data.valid,
            batch_size=self.batch_size,
            pin_memory=True,
        )

    def test_dataloader(self) -> DataLoader:
        return DataLoader(
            self.data.test,
            batch_size=self.batch_size,
            shuffle=False,
            pin_memory=True,
        )

# Cell
def get_topk_idxs(probs, slide_idxs, k=1):
    order = np.lexsort((probs, slide_idxs))
    probs = probs[order]
    slide_idxs = slide_idxs[order]
    index = np.empty(len(probs), 'bool')
    index[-k:] = True
    index[:-k] = slide_idxs[k:] != slide_idxs[:-k]
    idxs = order[index]
    return idxs

# Cell
def get_max_probs(probs, slide_idxs):
    nmax = slide_idxs.max()
    out = np.empty(nmax+1)
    out[:] = np.nan
    order = np.lexsort((probs, slide_idxs))
    probs = probs[order]
    slide_idxs = slide_idxs[order]
    index = np.empty(len(probs), 'bool')
    index[-1] = True
    index[:-1] = slide_idxs[1:] != slide_idxs[:-1]
    out[slide_idxs[index]] = probs[index]
    return out

